import React, { Component, handleSubmit } from 'react';
import { Form, FormField, Button, TextInput, Box } from 'grommet';

export default class InputForm2 extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.state = {
            Device1: '',
            Device2: '',
            Device3: ''
        }
    }
 
handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
}

handleFormSubmit(e) {
    e.preventDefault()
   localStorage.setItem('document',JSON.stringify(this.state));
}
 
componentDidMount() {
    this.documentData = JSON.parse(localStorage.getItem('document'));
}

handleSubmit=() => {
    alert("Resert clicked")
}


render() {
    return (
        <>
        <Box justify="center">
        <Box direction="column" alignSelf="center"  border={{ color: 'brand', size: 'medium' }} pad="large"> 
      <Form key={2}>
        <FormField label="Ship Date" width="large">
          <TextInput placeholder="Enter Ship Date" name="Device1"  value={this.state.Device1} onChange={this.handleChange} />
        </FormField>
        <FormField label="MAWB">
          <TextInput placeholder="Enter MAWB" name="Device1"  value={this.state.Device1} onChange={this.handleChange} />
        </FormField>
        <FormField label="Tracking Number (HAWB/ BL)">
          <TextInput placeholder="Enter Tracking Number (HAWB/ BL)" />
        </FormField>
        <FormField label="Ship Ref No.">
          <TextInput placeholder="Enter Ship Ref No. " />
        </FormField>
        <FormField label="Carrier">
          <TextInput placeholder="Enter Carrier " />
        </FormField>
        <FormField label="Arrival Date">
          <TextInput placeholder="Enter Arrival Date " />
        </FormField>
        <FormField label="Mode (Courier/Cargo)">
          <TextInput placeholder="Enter Mode (Courier/Cargo) " />
        </FormField>
        <FormField label="CAN Date">
          <TextInput placeholder="Enter CAN Date" />
        </FormField>
        <FormField label="IGM#">
          <TextInput placeholder="Enter IGM#" />
        </FormField>
        <FormField label="IGM Date">
          <TextInput placeholder="Enter IGM Date" />
        </FormField>
        <FormField label="Inco-Term(FOB/CIF/EXW/DDP/DAT)">
          <TextInput placeholder="Enter Inco-Term(FOB/CIF/EXW/DDP/DAT)" />
        </FormField>
        <Button key={2} type="submit" alignSelf="center" primary label="Submit"  />
      </Form>
      </Box>

          </Box> 
       </> 
    )
}
}
