import React, { Component, handleSubmit } from 'react';
import { Form, FormField, Button, TextInput, Box } from 'grommet';

export default class InputForm7 extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.state = {
            Device1: '',
            Device2: '',
            Device3: ''
        }
    }
 
handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
}

handleFormSubmit(e) {
    e.preventDefault()
   localStorage.setItem('document',JSON.stringify(this.state));
}
 
componentDidMount() {
    this.documentData = JSON.parse(localStorage.getItem('document'));
}

handleSubmit=() => {
    alert("Resert clicked")
}


render() {
    return (
        <>
        <Box justify="center">
        <Box direction="column" alignSelf="center"  border={{ color: 'brand', size: 'small' }} pad="medium"> 
        <Form key={7}>
        <FormField label="Whether Asset is Debounded or Not" width="large">
          <TextInput placeholder="Enter Whether Asset is Debounded or Not" />
        </FormField>
        <FormField label="Purpose of Removal(Description)">
          <TextInput placeholder="Enter Purpose of Removal(Description)" />
        </FormField>
        <FormField label="Approved By">
          <TextInput placeholder="Enter Approved By" />
        </FormField>
        <FormField label="Asset Removal Date from Data Center">
          <TextInput placeholder="Enter Asset Removal Date from Data Center" />
        </FormField>
        <FormField label="Asset Description">
          <TextInput placeholder="Enter Asset Description" />
        </FormField>
        <FormField label="Removal of Asset - Serial No.">
          <TextInput placeholder="Enter Removal of Asset - Serial No." />
        </FormField>
        <FormField label="Types of Removal as per customs">
          <TextInput placeholder="Enter Types of Removal as per customs" />
        </FormField>
        <Button key={7} type="submit" alignSelf="center" primary label="Submit"  />
      </Form>

          </Box> 
          </Box> 
       </> 
    )
}
}
