import React, { Component, handleSubmit } from 'react';
import { Form, FormField, Button, TextInput, Box } from 'grommet';

export default class InputFormm extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.state = {
            Device1: '',
            Device2: '',
            Device3: ''
        }
    }
 
handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
}

handleFormSubmit(e) {
    e.preventDefault()
   localStorage.setItem('document',JSON.stringify(this.state));
}
 
componentDidMount() {
    this.documentData = JSON.parse(localStorage.getItem('document'));
}

handleSubmit=() => {
    alert("Resert clicked")
}


render() {
    return (
        <>
        <Box justify="center">
        <Box direction="column" alignSelf="center"  border={{ color: 'brand', size: 'small' }} pad="medium"> 
        <Form key={3}>
        <FormField label="CHA" width="large">
          <TextInput placeholder="Enter CHA" />
        </FormField>
        <FormField label="CTH">
          <TextInput placeholder="Enter CTH" />
        </FormField>
        <FormField label="Rate of Duty">
          <TextInput placeholder="Enter Rate of Duty" />
        </FormField>
        <FormField label="Ex-Rate">
          <TextInput placeholder="Enter Ex-Rate" />
        </FormField>
        <FormField label="Type of Shipment(FOC/ORP/LOAN)">
          <TextInput placeholder="Enter Type of Shipment(FOC/ORP/LOAN)" />
        </FormField>
        <FormField label="BOE Field Date">
          <TextInput placeholder="Enter BOE Field Date" />
        </FormField>
        <Button key={3} type="submit" alignSelf="center" primary label="Submit"  />
      </Form>

          </Box> 
          </Box> 
       </> 
    )
}
}
