import React, { Component, handleSubmit } from 'react';
import { Form, FormField, Button, TextInput, Box } from 'grommet';

export default class InputForm6 extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.state = {
            Device1: '',
            Device2: '',
            Device3: ''
        }
    }
 
handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
}

handleFormSubmit(e) {
    e.preventDefault()
   localStorage.setItem('document',JSON.stringify(this.state));
}
 
componentDidMount() {
    this.documentData = JSON.parse(localStorage.getItem('document'));
}

handleSubmit=() => {
    alert("Resert clicked")
}


render() {
    return (
        <>
        <Box justify="center">
        <Box direction="column" alignSelf="center"  border={{ color: 'brand', size: 'small' }} pad="medium"> 
        <Form key={6}>
        <FormField label="Invoice Proceed By" width="large">
          <TextInput placeholder="Enter Invoice Proceed By" />
        </FormField>
        <FormField label="Invoice Scanned Date">
          <TextInput placeholder="Enter Invoice Scanned Date" />
        </FormField>
        <FormField label="WOID#">
          <TextInput placeholder="Enter WOID#" />
        </FormField>
        <FormField label="Payment Due Date">
          <TextInput placeholder="Enter Payment Due Date" />
        </FormField>
        <FormField label="Remark, if any">
          <TextInput placeholder="Enter Remark, if any" />
        </FormField>
        <Button key={6} type="submit" alignSelf="center" primary label="Submit"  />
      </Form>

          </Box> 
          </Box> 
       </> 
    )
}
}
