import React, { useState } from 'react';
import { Box, Text, Grommet, Main, Heading, Button, Card, CardHeader,CardFooter,CardBody} from 'grommet';
import { Server, Magic } from 'grommet-icons';
import Foooter from './Foooter';
import Headerr from './Headerr';
import TabsForm from './TabsForm';
import TabsForm2 from './TabsForm2';
import CardsForm from './CardsForm';



function App(props) {

  const theme = {
    global: {
      colors: {
        brand: '#01A982',
      },
      font: {
        family: 'Roboto',
        size: '18px',
        height: '20px',
      },
    },
  };

  return (
    <Grommet theme={theme}>
      <Headerr />
      <Main pad="xlarge" margin={{ left: 'small', right: 'small'}} >
        <Box>
          <Heading margin={{ left: "small",top: "small", bottom: "none"}}>Hello, Jane!</Heading>
          <Text margin="small">Welcome to the HPE Asset Management Module.</Text>
        </Box>

        <Box direction="row" gap="large" pad="medium">
          <Card  height="small" width="medium" >
                <CardHeader>
                  <Button icon={<Magic />} onClick={() => {}} />
                </CardHeader>
                <CardBody pad={{horizontal: "small"}}>
                <Text weight="bold">Customs Annexure </Text>
                <Text size="xsmall">Part A, B, C, D, E, F, G</Text>
                </CardBody>
                <CardFooter pad="small"> 
                  <Button primary label="TabsForm" size="small" color="brand" onClick={() => setHide(true)}/>
                </CardFooter>
          </Card>
          <Card  height="small" width="medium" >
                <CardHeader>
                  <Button icon={<Server />} onClick={() => {}} />
                </CardHeader>
                <CardBody pad={{horizontal: "small"}}>
                <Text weight="bold">Asset Tracker</Text>
                <Text size="xsmall">Part A, B, C, D</Text>
                </CardBody>
                <CardFooter pad="small">
                  <Button primary label="TabsForm" size="small" color="brand" onClick={() => setHidee(true)}/>
                </CardFooter>
          </Card>
        </Box> 
      </Main>
      <Foooter />

    </Grommet>
  );
}

export default App;
