import React, { Component, handleSubmit } from 'react';
import { Form, FormField, Button, TextInput, Box } from 'grommet';

export default class InputFormm extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.state = {
            Device1: '',
            Device2: '',
            Device3: ''
        }
    }
 
handleChange= (e)=> {
    this.setState({[e.target.name]:e.target.value});
}

handleFormSubmit(e) {
    e.preventDefault()
   localStorage.setItem('document',JSON.stringify(this.state));
}
 
componentDidMount() {
    this.documentData = JSON.parse(localStorage.getItem('document'));
}

handleSubmit=() => {
    alert("Resert clicked")
}


render() {
    return (
        <>
        <Box justify="center">
        <Box direction="column" alignSelf="center"  border={{ color: 'brand', size: 'small' }} pad="medium"> 
          <Form key={1}>
            <FormField label="Requestor" width="large">
              <TextInput placeholder="Enter Requestor" name="Device1"  value={this.state.Device1} onChange={this.handleChange} />
            </FormField>
            <FormField label="PR Number" width="large">
              <TextInput placeholder="Enter PR Number" name="Device2"  value={this.state.Device2} onChange={this.handleChange}  />
            </FormField>
            <FormField label="PO Number" width="large">
              <TextInput placeholder="Enter PO Number" name="Device3"  value={this.state.Device3} onChange={this.handleChange} />
            </FormField>
            <Button key={1} type="submit" primary label="Submit" onClick={this.handleFormSubmit}  />
          </Form>
          </Box> 
          </Box> 
       </> 
    )
}
}
